function f = vect2str(x,dims)% function which takes a collective action and outputs it% as a readable stringif length(x)~=sum(dims)	error(['length of x different to sum of dims:  length(x)=',num2str(length(x)),...			'  sum(dims)=',num2str(sum(dims))]);end;str='[ ';for i=1:length(dims)		%for each player	str=[str,'['];	for j=1:dims(i)		%for each players actions		str=[str,num2str(x(sum(dims(1:(i-1)))+j))];		if j~=dims(i)			str=[str,','];		end;	end;	str=[str,'] '];end;str=[str,']'];f=str;