%% op_alph3%% this m file provides a measure of optimality% for the alphas in a game with nonidentical payoffs% this function should be minimised with respect to alpha%% the function returns the negative minimum of the NI function % at the next stage for given alphafunction f = op_alph3(alpha,x,z,dims,lowerbound,upperbound,A,B,toler)global constrfunx0 = (1-alpha) * x + alpha * z;			% this is the place we want to find Z(x)y0 = x0;					% first guess is where we areoptions = optimset('Diagnostics','off','Display','off','LargeScale', 'off', 'TolCon', toler(1), 'TolFun', toler(2), 'TolX', toler(3), 'algorithm', 'active-set');[y, nikval] = fmincon(@nik_iso, y0, [], [], [], [], lowerbound, upperbound, constrfun, options, x0, dims);f = -nikval;